import React from 'react';
import { Link } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';
import Loading from '../components/Loading';
import { getUser } from '../services/userAPI';
import './Profile.css';

class Profile extends React.Component {
  state = {
    loading: true,
    profile: {},
  };

  async componentDidMount() {
    const user = await getUser();
    this.setState({
      profile: user,
      loading: false,
    });
  }

  render() {
    const { loading, profile } = this.state;
    return (
      <>
        <Header />
        {loading ? <Loading /> : (
          <>
            <div className='profile-container'>
              <div className='profile-box'>
            <img className='profile-image' src={ profile.image } alt={ profile.name } />
            <h2 className='profile-title'>Profile</h2>
                <p className='profile-information'>Nome:</p>
                <p className='profile-infos'>{profile.name}</p>
                <p className='profile-infos'>E-mail:</p>
                <p className='profile-infos'>{profile.email}</p>
            <p className='profile-infos'>Descrição: {profile.description}</p>
            <div>
              <Link to="/profile/edit"><p>Editar perfil</p></Link>
            </div>
              </div>
              </div>
            <Footer />
            </>
        )}
      </>
    );
  }
}

export default Profile;
