import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './MusicCard.css';
import WaveSurfer from "wavesurfer.js";

class MusicCard extends Component {
  render() {
    var wavesurfer = WaveSurfer.create({
    container: '#waveform',
    waveColor: 'violet',
    progressColor: 'purple'
});
    const { previewUrl, trackName, trackId, artworkUrl100, favorita, checked } = this.props;
    return (
      <div className='music-card-container'>
        <p>{trackName}</p>
        <label htmlFor="favorita" className='checkbox-container'>
          Favorite
          <input
            id="favorita"
            name="favorita"
            data-testid={ `checkbox-music-${trackId}` }
            type="checkbox"
            onChange={ () => favorita(this.props) }
            checked={ checked }
          />
        </label>
        <img className='album-img' src={artworkUrl100} alt={trackName} />
        <div id="waveform"></div>
        <audio  className='audio-player' src={ previewUrl } controls>
          <track kind="captions" />
          O seu navegador n√£o suporta o elemento
          {' '}
          {/* <code>audio</code> */}
        </audio>
      </div>
    );
  }
}

MusicCard.propTypes = {
  favorita: PropTypes.func.isRequired,
  checked: PropTypes.bool.isRequired,
  previewUrl: PropTypes.string.isRequired,
  trackId: PropTypes.number.isRequired,
  trackName: PropTypes.string.isRequired,
};

export default MusicCard;
