import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import getMusics from '../services/musicsAPI';
import MusicCard from '../components/MusicCard';
import { addSong } from '../services/favoriteSongsAPI';
import Loading from '../components/Loading';

class Album extends React.Component {
  state = {
    music: [],
    album: {},
    loading: false,
    best: [],
  };

  componentDidMount() {
    const { match } = this.props;
    const { params } = match;
    this.songList(params.id);
  }

  songList = async (id) => {
    const list = await getMusics(id);
    this.setState({
      album: list[0],
      music: list.slice(1),
    });
  };

  isChecked = (music) => {
      const { favorites } = this.state;
      return favorites.some((fav) => fav.trackId === music.trackId);
    };

  favorita = async (music) => {
    const { favorites } = this.state;
    if (isChecked(music)) {
      this.setState({ loading: true });
      await removeSong(music);
      const favs = favorites.filter((e) => e.trackId !== music.trackId);
      this.setState({ loading: false, favorites: favs });
    } else {
      this.setState({ loading: true });
      await addSong(music);
      this.setState((prevState) => ({ loading: false,
        favorites: [...prevState.favorites, music] }));
    }
  };

  render() {
    const { album, music } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        {loading ? <Loading /> : (
          <>
            <h2 data-testid="artist-name">{album.artistName}</h2>
            <img src={ album.artworkUrl100 } alt="" />
            <h3 data-testid="album-name">{album.collectionName}</h3>
            {music.map((item) => <MusicCard key={ item.trackName } { ...item } />)}
          </>
        )}
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }).isRequired,
};

export default Album;
