import React from 'react';
import PropTypes from 'prop-types';
import { createUser } from '../services/userAPI';
import Loading from './components/Loading'

class Login extends React.Component {
state = {
  name: '',
  loading: false,
  btnDisabled: true,
}
  enableBtn = (event) => {
    const minCaracteres = 3;
    return event.target.value.lenth >= minCaracteres
      ? this.setState({ btnDisabled: false, name: event.target.value })
      : this.setState({ btnDisabled: true, name: event.target.value });
  }

