          >
          </input>
        </label>
        <button
          type="button"
          data-testid="login-submit-button"
          disabled={ this.enableBtn }
          onClick={ this.handleLogin }
        >
          Entrar
        </button>
      </div>
    );
  }
}

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default Login;
