import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Loading from '../components/Loading';
import './Search.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';



class Search extends React.Component {
  state = {
    loading: false,
    search: '',
    artista: '',
    albumList: [],
    btnDisable: true,
    frase: false,
  };

  handleInput = (event) => {
    const { value } = event.target;
    const minCaracteres = 2;
    const btn = value.length < minCaracteres;
    this.setState({
      search: value,
      btnDisable: btn,
    });
  };

  searchAlbunsAndArtist = async () => {
    const { search } = this.state;
    this.setState({
      loading: true,
      search,
    });
    const albumArtista = await searchAlbumsAPI(search);
    this.setState({
      loading: false,
      artista: search,
      albumList: albumArtista,
      frase: true,
    });
  };

  render() {
    const { loading, albumList, artista, btnDisable, frase } = this.state;
    return (
      <div data-testid="page-search" className='search-container'>
        <Header />
        {loading ? <Loading /> : (
          <>
            <FontAwesomeIcon icon="fa-sharp fa-regular fa-magnifying-glass" />
            <label htmlFor="search">
              <input
                id="search"
                name="search"
                type="text"
                data-testid="search-artist-input"
                onChange={this.handleInput}
              // value={search}
              />
            </label>
            <button
              type="button"
              data-testid="search-artist-button"
              disabled={ btnDisable }
              onClick={ this.searchAlbunsAndArtist }
            >
              Pesquisar
            </button>
          </>
        )}
        {((albumList.length > 0)) ? (
          <>
            <h2>{`Resultado de álbuns de: ${artista}`}</h2>
            {albumList.map((e) => (
              <Link
                key={ e.collectionId }
                to={ `/album/${e.collectionId}` }
                data-testid={ `link-to-album-${e.collectionId}` }
              >
                <p>{e.collectionName}</p>
                <img src={ e.artworkUrl100 } alt="" />
              </Link>
            ))}
          </>
        ) : (frase && <p>Nenhum álbum foi encontrado</p>)}
      </div>
    );
  }
}

export default Search;
