import React from 'react';
import Header from '../components/Header';
import Loading from '../components/Loading';
import MusicCard from '../components/MusicCard';
import { getFavoriteSongs, removeSong } from '../services/favoriteSongsAPI';

class Favorites extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      favList: [],
    };
  }

  async componentDidMount() {
    const list = await getFavoriteSongs();
    this.setState({ loading: false, favList: list });
  }

  // favoriteList = async () => {
  //   this.setState({ loading: true });
  //   await removeSong();
  //   const list1 = await getFavoriteSongs();
  //   this.setState({ favList: list1, loading: false });
  // };

  // favoriteList = ({ target: { name } }) => {
  //   this.setState({ loading: true }, () => {
  //     const { favList } = this.state;
  //     const obj = favList.find((musica) => +musica.trackId === +name);
  //     this.removerFav(obj);
  //   });
  // };

  render() {
    const { loading, favList } = this.state;

    return (
      <>
        {loading && <Loading />}
        {!loading && (
          <div>
            <Header />
            <div data-testid="page-favorites">Favorites</div>
            {favList.map((fav) => (<MusicCard
              { ...fav }
              key={ fav.trackId }
              checked
              favoriteList={ this.favoriteList }
            />))}
          </div>
        )}
      </>
    );
  }
}

export default Favorites;
