import React from 'react';
import { getUser } from '../services/userAPI';
import Loading from './Loading';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      user: {},
    };
  }

  handleLoad = async () => {
    const loginName = getUser();
    await (this.setState({
      loading: false,
      user: loginName,
    }));
  };

  render() {
    const { loading, user } = this.state;
    if (loading) this.handleLoad();
    return (
      <header data-testid="header-component">
        {loading ? <Loading /> : (<p data-testid="header-user-name">
          {user.loginName}
        </p>)}
      </header>
    );
  }
}

export default Header;
