import React from 'react';
import Header from '../components/Header';
import Loading from '../components/Loading';
import MusicCard from '../components/MusicCard';
import { getFavoriteSongs, removeSong } from '../services/favoriteSongsAPI';

class Favorites extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      favList: [],
    };
  }

  async componentDidMount() {
    const data = await getFavoriteSongs();
    this.setState({ loading: false, favList: data });
  }

  favoriteList = async (music) => {
    this.setState({ loading: true });
    await removeSong(music);
    const data = await getFavoriteSongs();
    this.setState({ favList: data }, () => this.setState({ loading: false }));
  };

  render() {
    const { loading, favList } = this.state;

    return (
      <>
            <Header />
            <div data-testid="page-favorites">Favorites</div>
            {favList.map((fav) => (<MusicCard
              { ...fav }
              key={ fav.trackId }
              checked
              favoriteList={ this.favoriteList}
            />))}
      </>
    );
  }
}

export default Favorites;
