import React from 'react';
import PropTypes from 'prop-types';
import Loading from '../components/Loading';
import { createUser } from '../services/userAPI';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      name: ' ',
      password: '',
      loading: false,
      btnDisable: true,
    };
  }

  handleLogin = async () => {
    const { history } = this.props;
    const { name } = this.state;
    this.setState({ loading: true });
    createUser({ name }).then(() => history.push('/search'));
  };

  handleInput = (event) => {
    const { value } = event.target;
    const minCaracteres = 3;
    const name = value.length < minCaracteres;
    this.setState({
      name,
    });
  };

  handlePassword = (event) => {
    const { value } = event.target;
    const minCaracteres = 6;
    const pass = value.length < minCaracteres;
    this.setState({
      password: pass,
    });
  };

  render() {
    const { loading, btnDisable, name, password } = this.state;
    return (
      <div data-testid="page-login">
        {loading ? <Loading /> : (
          <>
            <h2>Login</h2>
            <label htmlFor="name">
              <input
                id="name"
                type="email"
                data-testid="login-name-input"
                placeholder= 'e-mail'
                onChange={ this.handleInput }
                // value={ name }
              />
            </label>
            <label htmlFor="password">
              <input
                id="password"
                type="password"
                data-testid="login-name-input"
                placeholder= 'senha'
                onChange={ this.handleInput }
                // value={ name }
              />
            </label>
            <button
              type="button"
              data-testid="login-submit-button"
              disabled={() => this.setState ({ name })}
              onClick={ this.handleLogin }
            >
              Entrar
            </button>
          </>
        )}
      </div>
    );
  }
}

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default Login;
