import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI'

class Search extends React.Component {
  state = {
    loading: false,
    search: '',
    artista: '',
    albuns: [],
    btnDisable: true,
  }

  handleInput = (event) => {
    const { value } = event.target;
    const minCaracteres = 2;
    const btn = value.length <= minCaracteres;
    this.setState({
      name: value,
      btnDisable: btn,
    });
  };

  searchAlbunsAndArtist = async () => {
    const { search } = this.state;
    this.setState({
      loading: true,
      search: '',
    });
    const albuns = await searchAlbumsAPI(search);
    this.setState({
      loading: false,
      artista: search,
      albuns,
    });
  };

  render() {
    const { search, loading, albuns, artista, btnDisable } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        {loading ? <Loading /> : (
          <>
            <label htmlFor="search">
              <input
                id="search"
                name="search"
                type="text"
                data-testid="search-artist-input"
                onChange={this.handleInput}
                value={search}
              />
            </label>
            <button
              type="button"
              data-testid="search-artist-button"
              disabled={btnDisable}
              onClick={this.searchAlbunsAndArtist}
            >
              Pesquisar
            </button>
          </>
        )}
      </div>
    );
  }
}
export default Search;
