import React from 'react';
import PropTypes from 'prop-types';
import { createUser } from '../services/userAPI';

class Login extends React.Component {
state = {
  login: '',
  loading: false,
  btnDisabled: true,
}
  enableBtn = (event) => {
    const { login, loading, btnDisabled } = this.state;
    const minCaracteres = 3;
    return event.target.value.lenth >= minCaracteres
      ? this.setState({ btnDisabled: false, login: event.target.value })
        : this.setState({ btnDisabled: true, login: event.target.value })
  }

  render() {
    const { login, loading, btnDisabled } = this.state;
    return (
      <div data-testid="page-login">
        <h2>Login</h2>
        <label htmlFor="name">
          <input
            id="name"
            type="text"
            data-testid="login-name-input"
            onChange={this.handleInput}
            value={login}
          >
          </input>
        </label>
        <button
          type="button"
          data-testid="login-submit-button"
          disabled={!this.enableBtn()}
          onClick={this.handleUser}
        >
          Entrar
        </button>
      </div>
    );
  }
}

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default Login;
